fn test_acvp_mct(){
    let mut data: [u8; 20] = [0xA9, 0x5D, 0x36, 0x95, 0x2A, 0x37, 0xCA, 0x88, 0xF7, 0x06, 0x4A, 0xF1, 0xC6, 0xA5, 0x72, 0x5B, 0x50, 0x17, 0xE5, 0xCC, ];
    let mut a: [u8; 20] = [0; 20];
    let mut b: [u8; 20] = [0; 20];
    let mut c: [u8; 20] = [0; 20];
    let mut msg: [u8; 60] = [0; 60];
    let mut digest_out: [u8; 20] = [0; 20];

    for ol in 0..100{
        a = data;
        b = data;
        c = data;
        for il in 0..1000{
            if il % 100 == 0 {
                println!("ol:{} il:{}", ol, il);
            }
            msg[0..SHA1_HASH_SIZE].copy_from_slice(&a);
            msg[SHA1_HASH_SIZE..SHA1_HASH_SIZE*2].copy_from_slice(&b);
            msg[SHA1_HASH_SIZE*2..SHA1_HASH_SIZE*3].copy_from_slice(&c);
            let digest = Sha1::default().digest(&msg).unwrap();
            digest_out = <[u8;SHA1_HASH_SIZE]>::from(digest);
            a = b;
            b = c;
            c = digest_out;
        }
        println!("");
        println!("MCT ol:{}", ol);
        for &elem in &digest_out {
            println!("SHA1:{:02X}", elem);
        }
        data = digest_out;
    }
}

