        field_combo.fifo_regs.itrng_fifo_status.data_count.next = next_c;
        field_combo.fifo_regs.itrng_fifo_status.data_count.load_next = load_next_c;
    end
    always_ff @(posedge clk) begin
        if(rst) begin
            field_storage.fifo_regs.itrng_fifo_status.data_count.value <= 10'h0;
        end else if(field_combo.fifo_regs.itrng_fifo_status.data_count.load_next) begin
            field_storage.fifo_regs.itrng_fifo_status.data_count.value <= field_combo.fifo_regs.itrng_fifo_status.data_count.next;
