    end
    assign hwif_out.fifo_regs.itrng_fifo_status.data_count.value = field_storage.fifo_regs.itrng_fifo_status.data_count.value;

    //--------------------------------------------------------------------------
    // Write response
    //--------------------------------------------------------------------------
    assign cpuif_wr_ack = decoded_req & decoded_req_is_wr;
    // Writes are always granted with no error response
    assign cpuif_wr_err = '0;

    //--------------------------------------------------------------------------
